INSERT INTO `ecs_article` ( `article_id`, `cat_id`, `title`, `content`, `author`, `author_email`, `keywords`, `article_type`, `is_open`, `add_time`, `file_url`, `open_type`, `link`, `description`, `click_count`, `goods_link` ) VALUES  ('39', '4', '当你想尽尽孝心的时候，可以送爸妈这些', '<div class=\"content\">\r\n	<p>\r\n		<span style=\"font-family:Microsoft YaHei;font-size:14px;\">说到尽孝心，小礼君有些惭愧，策划了无数的朋友的生日惊喜，却总是忽略了爸爸妈妈。直到\r\n两年前某一天，妈妈酸酸的说一句：你对你的朋友好到妈妈都有些嫉妒了。才幡然醒悟这么多年，最值得我去对他们好的人，我反而什么都没有做。于是才在近两年\r\n努力弥补，离家在外无法贴身照顾，只好买买买。说到给父母买礼物，很多朋友总是很愁不知道送什么，其实送给父母的礼物不用那么多华丽丽和精美包装，贴心实\r\n用最重要。今天小礼君就给大家整理了一份送给爸爸妈妈的礼物，当你想尽尽孝心的时候，就送他们这些吧。</span>\r\n	</p>\r\n	<h2>\r\n		<span style=\"font-family:Microsoft YaHei;font-size:14px;\">推荐一：送给永远年轻美丽的妈妈</span>\r\n	</h2>\r\n	<p>\r\n		<span style=\"font-family:Microsoft YaHei;font-size:14px;\">妈妈是个美人，时光你别伤害她。送给妈妈的礼物，适合她的化妆品是首选，好的化妆品能让妈妈老的慢一些，给她很多女人的自信。其次就是各种家居工具，让辛劳的妈妈做家务的时候，可以不用那么辛苦。</span>\r\n	</p>\r\n	<h3>\r\n		<span style=\"font-family:Microsoft YaHei;font-size:14px;\">1.让皱纹来的慢一些·Olay淡纹大红瓶</span>\r\n	</h3>\r\n	<p>\r\n		<span style=\"font-family:Microsoft YaHei;font-size:14px;\">我们在慢慢长大，妈妈在慢慢变老，皱纹无声无息爬上了她的脸。这款Olay大红瓶口碑超级好，简直是业界良心，油皮擦上去都不会觉得油腻，对皮肤有很好的提拉作用。很多人都表示用了之后明显感觉到皮肤在变好。送给妈妈，让皱纹来得慢一些吧。</span>\r\n	</p>\r\n	<p>\r\n		<img src=\"http://liwushuo-data.qiniudn.com/image/141127/stybdiyn4.jpg-w640\" alt=\"\" />\r\n	</p>\r\n	<p class=\"item-info\">\r\n		<span style=\"font-family:Microsoft YaHei;font-size:14px;\" class=\"item-info-price\">价格：218.00元</span><a class=\"item-info-link\" href=\"http://s.click.taobao.com/t?e=m%3D2%26s%3D3dcPLEq%2BxaccQipKwQzePOeEDrYVVa64pRe%2F8jaAHci5VBFTL4hn2bcuF%2F1AHuw8IfNVgZHErC5sOJRC4t67ybSnlAIVMwbDnEVXIc3MIL637BxdGR97IqXWh8VTXBJzrdQHK%2B24rxT4M0WZGAW3KMYOae24fhW0\" target=\"_blank\"><span style=\"font-family:Microsoft YaHei;font-size:14px;\">查看详情</span></a>\r\n	</p>\r\n	<h3>\r\n		<span style=\"font-family:Microsoft YaHei;font-size:14px;\">2.不要鱼尾纹·欧莱雅眼霜</span>\r\n	</h3>\r\n	<p>\r\n		<span style=\"font-family:Microsoft YaHei;font-siz