INSERT INTO `ecs_article` ( `article_id`, `cat_id`, `title`, `content`, `author`, `author_email`, `keywords`, `article_type`, `is_open`, `add_time`, `file_url`, `open_type`, `link`, `description`, `click_count`, `goods_link` ) VALUES  ('37', '16', '配送范围', '<div class=\"content_list\" style=\"padding:20px 8px;margin:0px;color:#666666;font-family:Arial, Helvetica, sans-serif, 宋体;\">\r\n	<p style=\"text-indent:2em;\">\r\n		<br />\r\n	</p>\r\n</div>', '', '', '', '0', '1', '1426373070', '', '0', 'http://', '', '2', 'http://');
INSERT INTO `ecs_article` ( `article_id`, `cat_id`, `title`, `content`, `author`, `author_email`, `keywords`, `article_type`, `is_open`, `add_time`, `file_url`, `open_type`, `link`, `description`, `click_count`, `goods_link` ) VALUES  ('41', '4', '科学饮食，摆脱高血压困扰', '<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-size:18px;line-height:1.5;\">现在</span><span style=\"font-size:18px;line-height:1.5;\">患有高血压</span><span style=\"font-size:18px;line-height:1.5;\">的中老年人数在不断上涨，部分患者选择在医院进行药物治疗，更多的人还是选择在家里进行饮食上的调理。因为是药三分毒，只有在饮食上面做足了功夫，才能够彻底去除高血压的病根。今天我们就来讲讲，调理高血压，该吃什么，不该吃什么。</span></span> \r\n</p>\r\n<p>\r\n	<br />\r\n<span style=\"font-size:16px;\"></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:18px;line-height:1.5;\">第一，当然是限制脂肪的摄入。膳食中应限制动物脂肪的摄入，烹调时多采用植物油。可多吃一些鱼，海鱼含有不饱和脂肪酸，能使胆固醇氧化，从而降低胆固醇，预防中风。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><br />\r\n</span> \r\n</p>\r\n<p align=\"center\">\r\n	<span style=\"font-size:16px;\"><img src=\"/images/upload/image/20150415/20150415215757_96231.jpg\" alt=\"\" /></span> \r\n</p>\r\n<p align=\"center\">\r\n	<span style=\"font-size:16px;\"><br />\r\n</span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n<span style=\"font-size:16px;\"></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:18px;line-height:1.5;\">第二，适量摄入蛋白质。高血压病人每日摄入蛋白质的量为每公斤体重1克为宜。其中植物蛋白占50%，最好用大豆蛋白。每周还应吃2-3次鱼类蛋白质，可改善血管弹性和通透性，增加尿钠排出，从而降低血压。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><br />\r\n</span> \r\n</p>\r\n<p align=\"center\">\r\n	<span style=\"font-size:16px;\"><img src=\"/images/upload/image/20150415/20150415220424_59702.png\" alt=\"\" height=\"248\" width=\"672\" /></span> \r\n</p>\r\n<p align=\"center\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-size:18px;\"><a target=\"_blank\" href=\"http://101.200.80.236/category.php?id=88\"><span style=\"color:#E53333;\"><u><br />\r\n</u></span></a></span></span> \r\n</p>\r\n<p align=\"center\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-size:18px;\"><a target=\"_blank\" href=\"http://101.200.80.236/category.php?id=88\"><span style=\"color:#E53333;line-height:1.5;\"><u>查看详情&gt;&gt;</u></span></a><br />\r\n</span></span> \r\n</p>\r\n<p>\r\n	<br />\r\n<span style=\"font-size:16px;\"><span style=\"font-size:18px;\"></span></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-size:18px;\"><br />\r\n</span></span> \r\n</p>\r\n<p align=\"left\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-size:18px;line-height:1.5;\">第三，</span><span style=\"font-size:18px;line-height:1.5;\">饮食宜清淡</span><span style=\"font-size:18px;line-height:1.5;\">，降低食盐量。吃钠盐过多是高血压的致病因素，而控制钠盐摄入量有利于降低和稳定血压。临床试验表明，对高血压病人每日食盐量由原来的10.5克降低到4.7—5.8克，可使收缩压平均降低4—6毫米汞柱。</span></span> \r\n</p>\r\n<p align=\"left\">\r\n	<br />\r\n<span style=\"font-size:16px;\"><span style=\"font-size:18px;\"></span></span> \r\n</p>\r\n<p align=\"center\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-size:18px;\"><img src=\"/images/upload/image/20150415/20150415221603_12161.jpg\" alt=\"\" /><br />\r\n</span></span> \r\n</p>\r\n<p>\r\n	<br />\r\n<span style=\"font-size:16px;\"><span style=\"font-size:16px;\"></span></span> \r\n</p>\r\n<p>\r\n	<br />\r\n<span style=\"font-size:16px;\"><span style=\"font-size:16px;\"></span></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-size:16px;\"><br />\r\n</span></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:18px;line-height:1.5;\">第四，控制热能的摄入。提倡吃复合糖类，如淀粉、面粉、玉米、小米、燕麦等植物纤维较多的食物，促进肠道蠕动，有利于胆固醇的排泄。</span> \r\n</p>\r\n<p>\r\n	<br />\r\n<span style=\"font-size:16px;\"></span> \r\n</p>\r\n<span style=\"font-size:18px;\"></span> \r\n<div class=\"artical-body\">\r\n	<p align=\"center\">\r\n		<span style=\"font-size:18px;\"><img src=\"/images/upload/image/20150415/20150415222541_11055.jpg\" alt=\"\" /></span> \r\n	</p>\r\n	<p align=\"center\">\r\n		<span style=\"font-size:18px;\"><a target=\"_blank\" href=\"http://101.200.80.236/category.php?id=163\"><span style=\"color:#E53333;\"><br />\r\n</span></a></span> \r\n	</p>\r\n	<p align=\"center\">\r\n		<span style=\"font-size:18px;\"><a target=\"_blank\" href=\"http://101.200.80.236/category.php?id=163\"><span style=\"color:#E53333;line-height:1.5;\">查看详情&gt;&gt;</span></a><br />\r\n</span> \r\n	</p>\r\n	<p>\r\n		<br />\r\n	</p>\r\n	<p>\r\n		<br />\r\n<span style=\"font-size:18px;\"></span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:18px;line-height:1.5;\">第五，多吃含钾、钙丰富而含钠低的食物，如土豆、芋头、茄子、海带、莴笋、冬瓜、西瓜等。因钾盐能促使胆固醇的排泄，增加血管弹性，有利尿作用，有利于改善心肌收缩能力。含钙丰富的食品如牛奶、酸牛奶、芝麻酱、虾皮、绿色蔬菜等，对心血管有保护作用。</span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:18px;line-height:1.5;\"><br />\r\n</span> \r\n	</p>\r\n	<p align=\"center\">\r\n		<span style=\"font-size:18px;line-height:1.5;\"><img src=\"/images/upload/image/20150415/20150415223446_30924.png\" alt=\"\" /></span> \r\n	</p>\r\n	<p align=\"center\">\r\n		<br />\r\n<span style=\"font-size:18px;line-height:1.5;\"></span> \r\n	</p>\r\n	<p align=\"center\">\r\n		<span style=\"font-size:18px;line-height:1.5;\"><br />\r\n</span> \r\n	</p>\r\n	<p>\r\n		<br />\r\n<span style=\"font-size:18px;\"></span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:18px;line-height:1.5;\">第六，多吃绿色蔬菜和新鲜水果，有利于心肌代谢，改善心肌能力和血液循环，促使胆固醇的排泄，防止高血压病的发展。上面所说的七个关于高血压的饮食禁忌的详细内容，相信各位朋友对于它上面所说的几种饮食禁忌都有一定的了解了吧。对于高血压患者朋友在治疗的期间，做好上面所说的饮食调理，多做一些运动，保持一个良好的心态，这对于病情的缓解以及好转有一定的帮助。</span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:18px;line-height:1.5;\"><br />\r\n</span> \r\n	</p>\r\n	<p align=\"center\">\r\n		<span style=\"font-size:18px;line-height:1.5;\"><img src=\"/images/upload/image/20150415/20150415223306_25827.jpg\" alt=\"\" /><br />\r\n</span> \r\n	</p>\r\n	<p>\r\n		<br />\r\n	</p>\r\n	<p>\r\n		<br />\r\n<span style=\"font-size:18px;\"></span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:18px;line-height:1.5;\">通过上面的食疗方法的介绍，大家会不会觉得其实高血压并不是那么恐怖的一种疾病。虽然高血压的突发性很大，但是只要你能够遵循医师的指导和在饮食上面的控制，高血压还是很容易被控制在安全的范围内的。</span> \r\n	</p>\r\n</div>\r\n<br />\r\n<p>\r\n	<br />\r\n</p>', '大孝', '', '高血压，饮食', '0', '1', '1429031350', 'data/article/1429075561164419244.jpg', '2', 'http://', '', '21', 'http://');
INSERT INTO `ecs_article` ( `article_id`, `cat_id`, `title`, `content`, `author`, `author_email`, `keywords`, `article_type`, `is_open`, `add_time`, `file_url`, `open_type`, `link`, `description`, `click_count`, `goods_link` ) VALUES  ('42', '4', '爱在细微处——春季，和爸妈一起应对高血压', '<p class=\"MsoNormal\">\r\n	<span style=\"font-size:16px;\">春季温差大，天气冷热不定，人体血压也容易因波动而升高，加之这个季节容易导致肝火旺、脾气大，因此春季是高血压患病的高发季节。对于中老年人，应对高血压是一个需要长期注意饮食和情绪的健康问题，今天“孝心执委会”给您推荐几招，帮您协助父母完善生活习惯，一起应对高血压。</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:16px;\">一、血压在24小时内是不断变化的，而一天中血压最高的时段是清晨时段，因此清晨血压可以看作管控全天24小时血压的风向标。通过监测清晨血压，可以有效的防范心脑血管事件的发生。“孝心执委会”建议您给爸妈在家里备一台有清晨高血压提醒功能的智能电子血压计，帮助爸妈形成每天清晨自测血压的良好习惯。</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<img src=\"/images/upload/image/20150416/20150416164423_23734.jpg\" alt=\"\" width=\"660\" height=\"440\" title=\"\" align=\"\" /> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:16px;\">详情</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:16px;\">二、肥胖是高血压的主要致病因素，有规律的体育运动可使血压下降，心率减慢，血浆尿酸、胆固醇和血糖减少，也是控制各种危险因素的重要措施。从高血压保健的角度，中老年人应在根据自己的年龄、身体状况及爱好来选取运动项目的同时，注意运动过程中的规律性和持之以恒。“孝心执委会”建议您给爸妈奉上一款能够实时监测运动量和运动效果的智能手环，既能帮父母及时掌握运动时间和运动效果，也能让他们将运动成果分享到社交圈，便于坚持运动，而且它还能监测父母的睡眠质量哦。</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<img src=\"/images/upload/image/20150416/20150416164614_89145.png\" alt=\"\" width=\"660\" height=\"280\" title=\"\" align=\"\" /> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<img src=\"/images/upload/image/20150416/20150416164639_88194.png\" alt=\"\" width=\"660\" height=\"452\" title=\"\" align=\"\" /> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:16px;\">详情</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:16px;\">三、大量资料显示，高血压病的发病率与膳食中钠盐摄入量呈显著正相关。而临床观察表明，不少轻度高血压病人，只需中度限制钠盐摄入，即可使其血压降至正常范围，部分对盐敏感的个体，对低盐饮食反应尤佳。因此“孝心执委会”建议您给爸妈准备低钠盐和量盐勺，让爸妈逐渐形成低盐饮食习惯。</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<img src=\"/includes/kindeditor/php/../../../images/upload/image/20150416/20150416164945_95973.jpg\" alt=\"\" width=\"330\" height=\"330\" title=\"\" align=\"\" /><img src=\"/includes/kindeditor/php/../../../images/upload/image/20150416/20150416164945_47280.jpg\" alt=\"\" width=\"330\" height=\"330\" title=\"\" align=\"\" /> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:16px;\">详情</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:16px;\">四、近年来的研究表明，多摄入优质蛋白质高血压的发病率下降，即使高钠饮食，只要摄入高质量动物蛋白，血压也不升高，在日常生活中一味强调素食来预防高血压是不可取的。动物蛋白质中鱼类蛋白质最好，“孝心执委会”建议您定期给爸妈购买一些深海鱼，既能促进钠的排泄，保护血管壁，也能提供氨基酸参与血压调节。</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<img src=\"/images/upload/image/20150416/20150416165251_19048.jpg\" alt=\"\" width=\"660\" height=\"496\" title=\"\" align=\"\" /> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:16px;\">详情</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:16px;\">五、调查结果显示，每日摄入5～7克钾，可使血压降低4～9mmHg，而每日食钙量小于300毫克者，高血压的发病率是每日食钙量大于1200毫克者2～3倍。豆类既含丰富的钾也有较多的钙，是中老年人补充钙和钾的极佳食物。“孝心执委会”建议您给爸妈家中备好各种豆类，让爸妈多吃豆类，适当补充钾和钙。</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<img src=\"/images/upload/image/20150416/20150416165322_97312.jpg\" alt=\"\" width=\"660\" height=\"528\" title=\"\" align=\"\" /> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:16px;\">详情</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:16px;\">六、忧愁、悲伤、焦虑、烦躁等不良情绪可明显影响血压，使血压升高。生活琐事纷扰、压力过大也是高血压常见的诱因。因此，保持心情舒畅，维持宽松、平和、乐观的健康心态，注意劳逸结合，保持充足睡眠，有利于维持高级神经中枢的正常功能，对预防及控制高血压能起到事半功倍的效果。“孝心执委会”建议您送爸妈一款有自动按摩功能的足浴盆，让爸妈每晚都能卸下一天的疲累与压力，放松身心，舒适享受！</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<img src=\"/images/upload/image/20150416/20150416171208_78682.jpg\" alt=\"\" width=\"660\" height=\"450\" title=\"\" align=\"\" /> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:16px;\">详情</span> \r\n</p>', '二孝', '', '高血压', '0', '1', '1429144502', 'data/article/1429148691787323113.jpg', '2', 'http://', '', '18', 'http://');
