INSERT INTO `ecs_brand` ( `brand_id`, `brand_name`, `brand_logo`, `brand_desc`, `site_url`, `sort_order`, `is_show` ) VALUES  ('1', '三星', '1424472757880496295.jpg', '三星电子（韩国语：삼성전자，英语：Samsung Electronics），韩国最大的电子工业公司，从事LCD、半导体、手机、数位媒体等业务，三星集团旗下的旗舰子公司，也是世界上营收最大的电子工业制造商。1938年3月它于朝鲜大邱成立，创始人是李秉喆，现任社长兼CEO是崔志成。', 'http://www.xiaoxinshuo.com', '50', '1');
INSERT INTO `ecs_brand` ( `brand_id`, `brand_name`, `brand_logo`, `brand_desc`, `site_url`, `sort_order`, `is_show` ) VALUES  ('2', '联想', '1424472942519192775.jpg', '联想集团有限公司（Lenovo，港交所：0992；OTCBB：LNVGY）成立于1984年，由中国科学院计算技术研究所投资20万元人民币、11名科技人员创办，当时称为“中国科学院计算所新技术发展公司”。1989年，成立“北京联想计算机集团公司”。联想公司主要生产笔记本电脑、一体机、台式电脑、服务器、手机、平板电脑、打印机、投影机以及其他移动互联、数码、电脑周边等类商品。2011年起成为全球第二大个人电脑生产商。2014年1月底，联想先后收购IBM的System x等服务器资产以及Blade Network Technology等系统网络资产和摩托罗拉的移动智能手机业务。', 'http://www.xiaoxinshuo.com', '50', '1');
